<?php// include headerinclude "header.php";$receivedName = mysqli_real_escape_string($connect, $_POST['receivedName']);$address = mysqli_real_escape_string($connect, $_POST['address']);$gender = $_POST['gender'];$provinceID = $_POST['provinceID'];$cityID = $_POST['cityID'];$zipCode = mysqli_real_escape_string($connect, $_POST['zipCode']);$phone = mysqli_real_escape_string($connect, $_POST['phone']);$cellPhone = mysqli_real_escape_string($connect, $_POST['cellPhone']);$createdDate = date('Y-m-d H:i:s');if ($receivedName != "" && $address != "" && $provinceID != "" && $cityID != "" && $cellPhone != "" && $gender != ""){	$queryShipping = "INSERT INTO as_shippings (memberID,												receivedName,												gender,												address,												provinceID,												cityID,												zipCode,												phone,												cellPhone,												createdDate,												modifiedDate)										VALUES(	'$sessMemberID',												'$receivedName',												'$gender',												'$address',												'$provinceID',												'$cityID',												'$zipCode',												'$phone',												'$cellPhone',												'$createdDate',												'')";	mysqli_query($connect, $queryShipping);		$insertID = mysqli_insert_id($connect);		$_SESSION['sessShippingID'] = $insertID;		echo json_encode("OK");}exit();?>