<?php// include headerinclude "header.php";$productID = mysqli_real_escape_string($connect, $_POST['productID']);$email = mysqli_real_escape_string($connect, $_POST['email']);$createdDate = date('Y-m-d H:i:s');if ($productID != "" && $email != ""){	$queryCheck = "SELECT * FROM as_product_email WHERE productID = '$productID' AND email = '$email' AND status = 'N'";	$sqlCheck = mysqli_query($connect, $queryCheck);	$numsCheck = mysqli_num_rows($sqlCheck);		if ($numsCheck == '0')	{		$queryEmail = "INSERT INTO as_product_email(productID,													email,													status,													createdDate)											VALUES(	'$productID',													'$email',													'N',													'$createdDate')";		mysqli_query($connect, $queryEmail);				$_SESSION['sessProductEmail'] = 1;	}	else	{		$_SESSION['sessProductEmail'] = 2;	}		echo json_encode("OK");}exit();?>