<?php// include headerinclude "header.php";$productID = mysqli_real_escape_string($connect, $_POST['productID']);$email = mysqli_real_escape_string($connect, $_POST['email']);$hp = mysqli_real_escape_string($connect, $_POST['hp']);$qty = mysqli_real_escape_string($connect, $_POST['qty']);$createdDate = date('Y-m-d H:i:s');if ($productID != "" && $email != "" && $qty != "" && $hp != ""){	$queryCheck = "SELECT * FROM as_request_order WHERE productID = '$productID' AND email = '$email' AND status = 'N'";	$sqlCheck = mysqli_query($connect, $queryCheck);	$numsCheck = mysqli_num_rows($sqlCheck);		if ($numsCheck == '0')	{		$queryEmail = "INSERT INTO as_request_order(productID,													email,													hp,													qty,													status,													createdDate)											VALUES(	'$productID',													'$email',													'$hp',													'$qty',													'N',													'$createdDate')";		$s = mysqli_query($connect, $queryEmail);				if ($s)		{			// showing up the contact			$queryProfile = "SELECT * FROM as_profiles WHERE profileID = '1'";			$sqlProfile = mysqli_query($connect, $queryProfile);			$dataProfile = mysqli_fetch_array($sqlProfile);						$subject2 = "Anaku.com - Anda mendapat request qty dari $email";			$html2 = "<p>Kepada Yth Admin Winespirit.com,</p>					<p>Pemberitahuan bahwa ada request qty dari $email sebanyak $qty btl, silahkan masuk ke dalam halaman administrator untuk melihat detil request</p>					<p>Anaku.com</p>";								// To send HTML mail, the Content-type header must be set			$headers2  = 'MIME-Version: 1.0' . "\r\n";			$headers2 .= 'Content-type: text/html; charset=iso-8859-1' . "\r\n";						// Additional headers			$headers2 .= 'From: Anaku.com <no-reply@Anaku.com>' . "\r\n";					mail($dataProfile['email'], $subject2, $html2, $headers2);		}				$_SESSION['sessProductEmail'] = 1;	}	else	{		$_SESSION['sessProductEmail'] = 2;	}		echo json_encode("OK");}exit();?>